			if legtask <= 10 && legtask >=0 {
				pool.Tune(10)
			}
			if legtask <= 50 && legtask > 10 {
				pool.Tune(50)
			}
			if legtask <= 100 && legtask >= 50 {
				pool.Tune(100)
			}
			if legtask <= 200 && legtask > 100 {
				pool.Tune(200)
			}
			time.Sleep(time.Second * 1)
		}
	}()
	go func() {
		for true {
			taskchannel <- time.Now().UnixNano()
			rand.Seed(time.Now().UnixNano())
			time.Sleep(time.Duration(rand.Intn(50)) * time.Millisecond)
		}
	}()
	for true {
		select {
		case data := <-taskchannel:
			pool.Submit(func() {
				task(data)
			})
		}
	}

}
func task(i int64) {
	rand.Seed(time.Now().UnixNano())
	time.Sleep(time.Duration(rand.Intn(3)) * time.Second)
	//log.Println("----", i)
}

